mapper.update:
update member
set nickname=#{nickname}, email=#{email}, tel=#{tel}
where user_id=#{userId}

update member
set type='승인대기'
where user_id=#{userId}

update expert_info
set major=#{major}, career=#{career},grade=#{grade},bank_name=#{bankName},account_no=#{accountNo},certificate=#{certificate},introduction=#{introduction},lang=#{lang},frame=#{frame}
where user_id=#{userId}

update member
set pwd=#{pwd}
where user_id=#{userId}

mapper.select:
select * from ( select rownum as RNUM, Z.* from ( select A.pd_no,A.user_id,B.pd_title , TO_CHAR(C.introduction) as introduction, D.pd_price, E.file_name from (((none_dup A join product B on A.pd_no=B.pd_no) join expert_info C on B.user_id=C.user_id) join pd_detail D on B.pd_no=D.pd_no) join pd_image E on B.pd_no=E.pd_no where D.pd_type='STANDARD' and A.user_id=#{userId}
) Z ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

	
select * from 
(
    select rownum as RNUM, Z.*
    from
        (
		select A.pd_no,B.form_no, A.user_id as s_user_id, B.user_id as b_user_id, A.pd_title, TO_CHAR(D.introduction) as introduction,B.price, A.regdate, A.read_count, C.pd_term, B.pay_flag, E.file_name
		from (((product A join form B
		on A.pd_no=B.pd_no) join pd_detail C
		on B.pd_no=C.pd_no and B.price=C.pd_price) join expert_info D
		on A.user_id=D.user_id) join pd_image E
		on A.pd_no = E.pd_no
		where B.user_id=#{userId}
		
        ) Z
    )
	where RNUM>#{firstRecordIndex} 
		
		and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

	
select * from 
(
    select rownum as RNUM, Z.*
    from
        (
		select A.pd_no,B.form_no, A.user_id as s_user_id, B.user_id as b_user_id, A.pd_title, TO_CHAR(D.introduction) as introduction,B.price, A.regdate, A.read_count, C.pd_term, B.pay_flag, E.file_name
		from (((product A join form B
		on A.pd_no=B.pd_no) join pd_detail C
		on B.pd_no=C.pd_no and B.price=C.pd_price) join expert_info D
		on A.user_id=D.user_id) join pd_image E
		on A.pd_no = E.pd_no
		where B.p_user_id=#{userId}
		
        ) Z
    )
	where RNUM>#{firstRecordIndex} 
		
		and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update form
set PAY_FLAG='Y'
where FORM_NO=#{formNo}
