mapper.select:
SELECT T1.PD_NO AS PD_NO , T1.USER_ID AS USER_ID , CATE_TYPE , PD_TITLE , REGDATE , DEL_TYPE , READ_COUNT , LANG , FRAME
, T2.DUP_FLAG AS DUP_FLAG FROM PRODUCT T1 LEFT OUTER JOIN NONE_DUP T2 ON T1.PD_NO = T2.PD_NO AND T2.USER_ID = #{userId}
, NULL AS DUP_FLAG FROM PRODUCT T1

mapper.insert:

	select PRODUCT_seq.nextval from dual

INSERT INTO PRODUCT
(
PD_NO
, USER_ID
, CATE_TYPE
, PD_TITLE
, LANG
, FRAME
)VALUES(
#{pdNo}
, #{userId}
, #{cateType}
, #{pdTitle}
, #{lang}
, #{frame}
)


	select PD_DETAIL_seq.nextval from dual

INSERT INTO PD_DETAIL
(

)VALUES(
#{detailNo}
, #{pdNo}
, #{pdType}
, #{pdPrice}
, #{pdTerm}
, #{editCount}
, #{serviceExplanation}
, #{editExplanation}
, #{commercial}
, #{codeSupply}
, #{payment}
, #{customize}
)


	select PD_IMAGE_seq.nextval from dual

INSERT INTO PD_IMAGE
(
PDIMG_NO
, PD_NO
, FILE_NAME
, ORIGINAL_FILE_NAME
, FILE_SIZE
, FILE_TYPE
)VALUES(
#{pdImgNo}
, #{pdNo}
, #{fileName}
, #{originalFileName}
, #{fileSize}
, #{fileType}
)
